import React from "react";
import "./Explore.css";
import { Row, Col } from "react-bootstrap";
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";
import Card from '../Card/Card'
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {shallowEqual, useSelector, useDispatch  } from "react-redux";

export default function Explore() {

  var settings = {
    dots: false,
    infinite: true,
    arrows: false,
    autoplay: true,
    autoplaySpeed: 2000,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    initialSlide: 0,
    cssEase: "linear",

    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };
  const Products = useSelector(state => state.changeState.getexplore)
  const AllProducts = useSelector(state => state.changeState.getproducts)

  return (
    <React.Fragment>
      <div className="live-auction">
        <div className="live-auction-arrow">
          <h6 className="live">Explore</h6>
          <p>  <AiOutlineArrowLeft />  <AiOutlineArrowRight /></p>
        </div>
        {Products.length===0 ?
            <Row>
            <Slider {...settings}>
  
              {AllProducts.map((x) =>
  
                <Col md={3}>
                  <Card
                    data={x} />
                </Col>
              )}
  
            </Slider>
          </Row>
          :
        <Row>
          <Slider {...settings}>

            {Products.map((x) =>

              <Col md={3}>
                <Card
                  data={x} />
              </Col>
            )}

          </Slider>
        </Row>
        }
      </div>
    </React.Fragment>
  );
}
